// create sessionId from Global Variables
const sessionId = $now
const workflowStaticData = $getWorkflowStaticData('global');
workflowStaticData.sessionId = sessionId;

// Return
return { enriched_tasks: $input.all(), already_scheduled: $('Repackage JSON').first().json.already_scheduled, sessionId: sessionId };

// Extract all goal IDs from the merged data
const goalIds = $input.all().map(item => item.json["Goals Id"]);

// Remove duplicates and create a comma-separated list for SQL IN clause
const uniqueGoalIds = [...new Set(goalIds)];
const goalIdList = uniqueGoalIds.map(id => `'${id}'`).join(', ');

// Return the goal IDs for the next query
return [{ json: { goalIds: uniqueGoalIds, goalIdList: goalIdList } }];

// Get the merged project/goal data and task data
const projectGoalData = $("Inner Join Project/Goals").all();
const taskData = $input.all();

// Create lookup maps for quick access
const projectMap = {};
const goalMap = {};

for (const item of projectGoalData) {
  const projectId = item.json["Active Project Id"];
  const goalId = item.json["Goals Id"];

  if (projectId) {
    projectMap[projectId] = {
      name: item.json["Project Name"],
      status: item.json["Status"],
      active: item.json["Active"]
    };
  }

  if (goalId) {
    goalMap[goalId] = {
      name: item.json["Goal name"],
      projectId: item.json["Parent Project"],
      status: item.json["Status"],
      scope: item.json["Scope"]
    };
  }
}

// Process tasks and enrich with human-readable names
const enrichedTasks = [];

for (const task of taskData) {
  const goalId = task.json["Goal Id"];
  const projectId = task.json["Project Id"];
  const goal = goalMap[goalId];
  const project = projectMap[projectId];

  enrichedTasks.push({
    json: {
      // Task data
      id: task.json["Task Id"],
      taskName: task.json["Task name"],
      description: task.json["Description"] || "[No Description Provided]",
      status: task.json["Status"],
      taskType: task.json["Task Type"],
      priority: task.json["Priority"],
      effortLevel: task.json["Effort level"],
      timeEstimate: task.json["Time Estimate"],
      dueDate: task.json["Due Date"],
      dateCompleted: task.json["Date Completed"],
      weekStartDate: task.json["Week Start Date"],
      assignee: task.json["Assignee"],

      // Goal data
      goalId: goalId,
      goalName: goal ? goal.name : "Unknown Goal",
      goalStatus: goal ? goal.status : null,
      goalScope: goal ? goal.scope : null,

      // Project data
      projectId: projectId,
      projectName: project ? project.name : "Unknown Project",
      projectStatus: project ? project.status : null,
      projectActive: project ? project.active : false,

      // Dependencies
      dependencyIds: task.json["Dependency IDs"],
      dependencyNames: task.json["Dependency Names"]
    }
  });
}

return { enrichedTasks: enrichedTasks };

const workflowStaticData = $getWorkflowStaticData('global')
const sessionId = $('Nest Into 1 Head Key-Value JSON').first().json.sessionId ?? workflowStaticData.sessionId;

return { sessionId: sessionId, data: $input.first().json }
