=UPDATE projects 
SET {{ $fromAI('set_clause', 'Raw SQL SET clause (e.g., "is_validated = true" or "time_estimate_months = 3, expansion_horizon = \'2 Weeks\'::expansion_horizon"). Use dollar quotes ($$) for text with quotes, single quotes for UUIDs/enums, no quotes for numbers/booleans.', 'string') }}
WHERE id = $${{ $fromAI('project_id', 'UUID of the project to update', 'string') }}$$
RETURNING *;

=INSERT INTO goals (
  name, 
  description, 
  status, 
  scope, 
  success_criteria,
  {{ $fromAI('due_date', 'Target completion date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') ? 'due_date,' : '' }}
  project_id{{ $fromAI('parent_goal_id', 'UUID of parent goal if this is a sub-goal (leave empty if not provided)', 'string', '') ? ', parent_goal_id' : '' }}
)
VALUES (
  '{{ $fromAI('goal_name', 'Concise name of the milestone/goal (3-8 words)', 'string') }}',
  $${{ $fromAI('goal_description', 'Detailed description including purpose and context', 'string') }}$$,
  'Not started',
  '{{ $fromAI('scope', 'Milestone scope: Monthly, Quarterly, or Weekly-Milestone', 'string', 'Monthly') }}'::goal_scope,
  $${{ $fromAI('success_criteria', 'Measurable outcomes that indicate completion', 'string') }}$$,
  {{ $fromAI('due_date', 'Target completion date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') ? "'" + $fromAI('due_date', 'Target completion date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') + "'" : '' }}{{ $fromAI('due_date', 'Target completion date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') ? ',' : '' }}
  '{{ $fromAI('project_id', 'UUID of the parent project', 'string') }}'{{ $fromAI('parent_goal_id', 'UUID of parent goal if this is a sub-goal (leave empty if not provided)', 'string', '') ? ", '" + $fromAI('parent_goal_id', 'UUID of parent goal if this is a sub-goal (leave empty if not provided)', 'string', '') + "'" : '' }}
)
RETURNING id::text, name, description, status::text, scope::text, success_criteria, due_date::text, project_id::text, parent_goal_id::text, created_at::text;

=UPDATE goals 
SET {{ $fromAI('set_clause', 'Raw SQL SET clause (e.g., "status = \'Active\'" or "success_criteria = $$Users can login and reset passwords$$, scope = \'Monthly\'::goal_scope"). Use dollar quotes ($$) for text with quotes, single quotes for UUIDs/enums, no quotes for numbers/booleans.', 'string') }}
WHERE id = '{{ $fromAI('goal_id', 'UUID of the goal to update', 'string') }}'
RETURNING *;

=DELETE FROM goals
WHERE id = '{{ $fromAI('goal_id', 'UUID of the goal to delete', 'string') }}'
RETURNING id::text, name, description, status::text, scope::text, success_criteria, due_date::text, project_id::text, created_at::text;

=INSERT INTO tasks (
  name,
  description,
  goal_id,
  {{ $fromAI('task_type', 'Task type: Network, Debug, Review, Develop, Marketing, Provision, or Research (leave empty if not provided)', 'string', '') ? 'task_type,' : '' }}
  {{ $fromAI('priority', 'Priority level: Low, Medium, or High (leave empty if not provided)', 'string', '') ? 'priority,' : '' }}
  {{ $fromAI('effort_level', 'Effort level: Small, Medium, or Large (leave empty if not provided)', 'string', '') ? 'effort_level,' : '' }}
  {{ $fromAI('time_estimate_minutes', 'Time estimate in MINUTES (60-480 minutes = 1-8 hours, leave empty if not provided)', 'string', '') ? 'time_estimate_minutes,' : '' }}
  {{ $fromAI('due_date', 'Due date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') ? 'due_date,' : '' }}
  {{ $fromAI('week_start_date', 'Week start date in YYYY-MM-DD format for week assignment (leave empty if not provided)', 'string', '') ? 'week_start_date,' : '' }}
  status
)
VALUES (
  '{{ $fromAI('task_name', 'Clear, actionable task name (verb + object, e.g., "Implement user login endpoint")', 'string') }}',
  $${{ $fromAI('task_description', 'Detailed description of what needs to be done and why', 'string') }}$$,
  '{{ $fromAI('goal_id', 'UUID of the goal this task belongs to', 'string') }}',
  {{ $fromAI('task_type', 'Task type: Network, Debug, Review, Develop, Marketing, Provision, or Research (leave empty if not provided)', 'string', '') ? "'" + $fromAI('task_type', 'Task type: Network, Debug, Review, Develop, Marketing, Provision, or Research (leave empty if not provided)', 'string', '') + "'::task_type," : '' }}
  {{ $fromAI('priority', 'Priority level: Low, Medium, or High (leave empty if not provided)', 'string', '') ? "'" + $fromAI('priority', 'Priority level: Low, Medium, or High (leave empty if not provided)', 'string', '') + "'::priority_level," : '' }}
  {{ $fromAI('effort_level', 'Effort level: Small, Medium, or Large (leave empty if not provided)', 'string', '') ? "'" + $fromAI('effort_level', 'Effort level: Small, Medium, or Large (leave empty if not provided)', 'string', '') + "'::effort_level," : '' }}
  {{ $fromAI('time_estimate_minutes', 'Time estimate in MINUTES (60-480 minutes = 1-8 hours, leave empty if not provided)', 'string', '') ? $fromAI('time_estimate_minutes', 'Time estimate in MINUTES (60-480 minutes = 1-8 hours, leave empty if not provided)', 'string', '') + ',' : '' }}
  {{ $fromAI('due_date', 'Due date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') ? "'" + $fromAI('due_date', 'Due date in YYYY-MM-DD format (leave empty if not provided)', 'string', '') + "'," : '' }}
  {{ $fromAI('week_start_date', 'Week start date in YYYY-MM-DD format for week assignment (leave empty if not provided)', 'string', '') ? "'" + $fromAI('week_start_date', 'Week start date in YYYY-MM-DD format for week assignment (leave empty if not provided)', 'string', '') + "'," : '' }}
  'Not started'::task_status
)
RETURNING id::text, name, description, status::text, task_type::text, priority::text, effort_level::text, time_estimate_minutes, due_date::text, week_start_date::text, goal_id::text, created_at::text;

=UPDATE tasks 
SET {{ $fromAI('set_clause', 'Raw SQL SET clause (e.g., "status = \'Done\'::task_status" or "time_estimate_minutes = 240, priority = \'High\'::priority_level"). Use dollar quotes ($$) for text with quotes, single quotes for UUIDs/enums, no quotes for numbers/booleans/dates.', 'string') }}
